class Solution:
    def twoSum(self,nums, target):
        hashmap={}
        for i,j in enumerate(nums):
            hashmap[j]=i
        for n,m in enumerate(nums):
            number=hashmap.get(target-m)
            if number is not None and n!=number:
                return[n,number]
        return[0,0]

